//COMPOSANT ENFANT
import React from 'react';
import Employe from './Employe';
import { Consumer } from '../context';

class ListeEmp extends React.Component {

    componentDidMount() {
        console.log('composant est assemblé')
    }

    // UNSAFE_componentWillMount(){
    //     console.log('composant va etre assemblé')
    // }

    render() {
        return (
            <Consumer>
                {value => {
                    if (value.employes.length === 0) {
                        return (
                            <div>
                               <h2>No employes!!! <br/> Please A</h2> 
                            </div>
                        )
                    }
                    //value = this.state ds context
                    console.log(value);
                    return (
                        <div>
                            {value.employes.sort((a, b) => a.name > b.name ? 1 : -1).map(employe => (
                                <Employe
                                    key={employe.id}
                                    id={employe.id}
                                    nom={employe.nom}
                                    poste={employe.poste}
                                    anciennete={employe.anciennete}
                                />
                            ))}
                        </div>
                    )
                }}
            </Consumer>
        )

    }
}

export default ListeEmp;
