//COMPOSANT ENFANT
import React from 'react';
import Employe from './Employe';

class ListeEmp extends React.Component {
    state = {
        employes : [
            {
                id : 1,
                nom : 'Dupond Dup',
                poste : 'developpeur',
                anciennete : '4ans' 
            },
            {
                id : 2,
                nom : 'Dupond Dup',
                poste : 'developpeur',
                anciennete : '4ans' 
            },
            {
                id : 3,
                nom : 'Dupond Dup',
                poste : 'developpeur',
                anciennete : '4ans' 
            }
          
        ],
    }

    delete = (id) => {
        const newEmployes = this.state.employes.filter(employe=>{
            employe.id !== id
        })
        this.setState({})
    }
    render() {
        return (
            <div>
                {this.state.employes.map(employe=>(
                    <Employe 
                    key = {employe.id}
                    nom = {employe.nom}
                    poste = {employe.poste}
                    anciennete = {employe.anciennete}
                    deleteClick={()=>this.delete(employe.id)}
                    />
                ))}
            </div>
        )
    }
}

export default ListeEmp;
