//COMPOSANT ENFANT
import React from 'react';
import Employe from './Employe';
import {Consumer} from '../context';

class ListeEmp extends React.Component {

    componentDidMount(){
        // console.log('composant est assemblé')
        this.setState({
            employes: this.props.employes
        })
    }

    state = {
        employes: [],
        sort: "asc"
    };


    toggleSort = () => {
        const {employes} = this.state;
        let newEmployes= employes.sort((a, b) => a.name.localeCompare(b.name));
        this.setState({ employes: newEmployes });
      };

    render() {
        const {employes} = this.state;

        return(
            <Consumer>
                {value=>{
                    //value = this.state ds context
                    return(
                        <div>
                        {value.employes.map(employe=>(
                            <Employe 
                            key = {employe.id}
                            id = {employe.id}
                            nom = {employe.nom}
                            poste = {employe.poste}
                            anciennete = {employe.anciennete}
                            />
                        ))}
                    </div>
                    )
                }}
            </Consumer>
        )
        
    }
}

export default ListeEmp;
