//COMPOSANT ENFANT
import React from 'react';
import Employe from './Employe';
import { Consumer } from '../context';
import { useState } from 'react';




    const sortArray = (type, state) => {


        const types = {
          albums: 'albums',
          members: 'members',
          formed: 'formed_in',
        };
        const sortProperty = types[type];
        const sorted = state.sort((a, b) => b[sortProperty] - a[sortProperty]);
        console.log(sorted);
        setData(sorted);

      
     }

class ListeEmp extends React.Component {
    componentDidMount() {
        console.log('composant est assemblé')
    }
    render() {
        return (
            <Consumer>
                {value => {
                    //value = this.state ds context
                    return (

                        <div>
                            <select onChange={(e) => sortArray(e.target.value)}>
                                <option value="nom">Nom</option>
                                <option value="anciennete">Anciennete</option>
                            </select>

                            {value.employes.sort((a, b) => (a.nom > b.nom) ? 1 : -1).map(employe => (
                                <Employe
                                    key={employe.id}
                                    id={employe.id}
                                    nom={employe.nom}
                                    poste={employe.poste}
                                    anciennete={employe.anciennete}
                                />
                            ))}
                        </div>
                    )
                }}
            </Consumer>
        )

    }
}

export default ListeEmp;
