//COMPOSANT ENFANT
import React from 'react';
import Employe from './Employe';
import {Consumer} from '../context';

class ListeEmp extends React.Component {
  

    delete = (id) => {
        const newEmployes = this.state.employes.filter(employe=>
            employe.id !== id
        )
        this.setState({
            employes: newEmployes
        })
    }


    render() {
        return(
            <Consumer>
                {value=>{
                    return(
                        <div>
                        {this.state.employes.map(employe=>(
                            <Employe 
                            key = {employe.id}
                            nom = {employe.nom}
                            poste = {employe.poste}
                            anciennete = {employe.anciennete}
                            deleteClick={()=>this.delete(employe.id)}
                            />
                        ))}
                    </div>
                    )
                }}
            </Consumer>
        )
        
    }
}

export default ListeEmp;
